#!/usr/bin/env python3

# for movie
from PIL import Image
from PIL import ImageFont
from PIL import ImageDraw

import numpy as np
from collections import deque
# Use intmachine from ../common
import sys
sys.path.insert(0,'../common')
from intmachine import Intmachine

# Read program from stdin
prog = []
with open('input.txt') as f:
    line = f.readline().rstrip()
for i in map(int, line.split(',')):
    prog.append(i)

# 0 for input, 1 for output
queues = []
for _ in range(2):
    dq = deque()
    queues.append(dq)

# Create and connect machine
m = Intmachine('d13', prog, queues[0], queues[1])

# We got dimensions of the playing board from part 1
width = 44
heigth = 22
score = 0
board = np.array([0] * width * heigth, dtype = np.int8)
oldball = False
# 20, 17 is starting point with direction 1, 1.
# This way direction will get correct directly.
ball = np.array([19, 16], dtype = np.int8)
paddle = np.array([22, 20], dtype = np.int8)

# Use ytab for faster index-into-board calculation
ytab = np.array([0] * heigth, dtype = np.int16)
for y in range(heigth):
    ytab[y] = y * width

def put_tile(x, y, tile):
    board[ytab[y] + x] = tile

def get_tile(x, y):
    return board[ytab[y] + x]

tiles = [' ', 'W', 'o', '_', 'O']
#tiles = [' ', '■', '■', '_', 'O']
# Set movie to False to play game manually in ascii mode
movie = True
if movie:
    font = ImageFont.truetype('/System/Library/Fonts//Supplemental/Andale Mono.ttf', 48)
    fontw, fonth = font.getsize('A' * width)
    fontonex = font.getsize('A')[0]
    # Of course use ZX Spectrum colors
    colors = [ (0, 0, 0), (0, 0xd7, 0), (0xd7, 0, 0), (0xff, 0xff, 00), (0xff, 0xff, 0xff) ]
frame = 0
def dump_board():
    global frame, score
    if movie:
        im = Image.new('RGB', (fontw, fonth * heigth))
        draw = ImageDraw.Draw(im)
        # Draw board
        for y in range(heigth):
            line = ''
            for x in range(width):
                tile = get_tile(x, y)
                draw.text((x * fontonex, y * fonth), tiles[tile], colors[tile], font=font)
        # Draw score
        scoremat = ' ' + '{:05d}'.format(score) + ' '
        draw.text((34 * fontonex, 0), scoremat, (0xff, 0xff, 0xff), font=font)
        # Save frame
        filename = 'out' + '{:04d}'.format(frame) + '.png'
        im.save(filename)
    else:
        # ascii art
        print('\033[2J\033[1;1H')
        print('score:', score)
        for y in range(heigth):
            for x in range(width):
                tile = get_tile(x, y)
                sys.stdout.write(tiles[tile])
            print('')
    frame += 1


def do_game(x, y, tile):
    global score, ball, oldball, paddle
    if x == -1 and y == 0:
        score = tile
        return None
    # 0 = empty
    # 1 = wall
    # 2 = block
    # 3 = paddle
    # 4 = ball
    if tile == 4:
        oldball = ball.copy()
        ball[:] = (x, y)

        # TODO: Check for collission here
        # nx = next_x_position()
        
        # Erase old ball 
        put_tile(oldball[0], oldball[1], 0)
    elif tile == 3:
        # Erase old paddle
        put_tile(paddle[0], paddle[1], 0)
        paddle[:] = (x, y)
    # Leave room for score in wallspace in row 0
    if y != 0 or (x < 34 or x > 40):
        put_tile(x, y, tile)

# poke 2 to addr 0 to start game
m.poke(0, 2)

