for i in inputs:
    m.push(i)

running = True
while running:
    while len(m.oq) < 3 and running:
        if m.would_stall():
            # read input from keyboard
            # e/u for left/right, any other key for 0
            c = input('')
            if c == 'e':
                i = -1
            elif c == 'u':
                i = 1
            else:
                i = 0
            m.push(i)
            inputs.append(i)
        running = m.do_op()
    if running:
        # Got three outputs
        x = m.pop()
        y = m.pop()
        tile = m.pop()
        do_game(x, y, tile)
        if tile == 4:
            # Only draw frames when ball moves
            dump_board()

